<script type="text/javascript">
$(function(){
	$(".ncs_buy").click(function(){
		
		var str;
		var group;
		str = $(this).attr('rev');
		str = str.split('_');
		group = str[0] + "_" + str[1] + "_" + str[2]; //获取主件组名
		
